import React from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";

// Import component
import Card from "./card";

function Forecast({todayForecast, forecasts}){

    // const trailProps = useTrail({
    //     from: {marginTop: "-75px", opacity: 0},
    //     to: {marginTop: "0px", opactiy: 1}
    // })

    const springProps = useSpring({
        from: {opacity: 0},
        to: {opacity: 1}
    })

    const trailProps = useTrail(6, {
        from: {opacity: 0,
               marginTop: "-75px"},
        to: {opacity: 1,
             margintTop: "0px"}
    })

    console.log(forecasts);

    return(
        <div style={{display: todayForecast[0].id == undefined ? "none" : null}} className="container">
            <div className="today-card-container">
                    <animated.div style={{opacity: springProps.opacity}} className="card">
                        <Card
                            key={todayForecast[0].id}
                            day={todayForecast[0].day}
                            timeOfDay={todayForecast[0].timeOfDay}
                            temp={todayForecast[0].temp}
                            cloudType={todayForecast[0].cloudsType}
                            clouds={todayForecast[0].clouds}
                            high={todayForecast[0].high}
                            low={todayForecast[0].low}
                        />
                    </animated.div>
            </div>

            <div className="cards-container">
                {trailProps.map((trailimation, index) => (
                     <animated.div style={{...trailimation}} className="card">
                        <Card
                            key={forecasts[index-1].id}
                            day={forecasts[index-1].day}
                            timeOfDay={forecasts[index-1].timeOfDay}
                            temp={forecasts[index-1].temp}
                            cloudType={forecasts[index-1].cloudsType}
                            clouds={forecasts[index-1].clouds}
                            high={forecasts[index-1].high}
                            low={forecasts[index-1].low}
                        />
                 </animated.div>
                ))
                }
            </div>
        </div>
    );
};

export default Forecast;
