import React, {useState} from "react";
import './App.css';

// Import component
import Forecast from "./components/forecast";

const API_key = "fae6fa0ed6e96f99493161cd7089d3a1";

function App() {
  const [inputText, setInputText] = useState("");
  const [form, setState] = useState({
    city: undefined,
    day: undefined,
    temp: undefined,
    clouds: undefined,
    high: undefined,
    low: undefined
  });

  function inputTextHandler(e){

    console.log(e.target.value);
    setInputText(e.target.value);
  };

  async function getWeatherData(e){
    e.preventDefault();
    var location = inputText;

    // make sure user inputs a valid city and state separated by comma
    if(location.includes(',')){
      var locArr = location.split(',');

      var city = locArr[0];
      var state = locArr[1];

      // fetch data from weather api
      const dataToday = await fetch(
        'https://api.openweathermap.org/data/2.5/weather?q='+city+','+state+'&appid='+API_key
      ).then((res) => res.json())
      .then((dataToday) => dataToday);

      var long = dataToday.coord.lon;
      var lat = dataToday.coord.lat;

      const data = await fetch(
        'https://api.openweathermap.org/data/2.5/onecall?lat='+lat+'&lon='+long+'&units=imperial&appid='+API_key
      ).then((res) => res.json())
      .then((data) => console.log(data));

      // Create array of unix timestamps that will be converted to weekdays
      var unixArr = [data.daily[0].dt, data.daily[1].dt, data.daily[2].dt, data.daily[3].dt, data.daily[4].dt];
      var weekDays = new Array(5);

      // Loop through array and convert each element from unix to weekday
      for (var i = 0; i < unixArr.length; i++){
        var dateObject = new Date(unixArr[i] * 1000);
        var day = dateObject.toLocaleString("en-US", {weekday: "long"});
        weekDays[i] = day;
      }

      setState({
        city: dataToday.name,

        day: [weekDays[0],
              weekDays[1],
              weekDays[2],
              weekDays[3],
              weekDays[4]],

        temp: [data.daily[0].temp.day,
              data.daily[1].temp.day,
              data.daily[2].temp.day,
              data.daily[3].temp.day,
              data.daily[4].temp.day],

        clouds: [data.daily[0].weather.description,
                data.daily[1].weather.description,
                data.daily[2].weather.description,
                data.daily[3].weather.description,
                data.daily[4].weather.description,],

        high: [data.daily[0].temp.max,
              data.daily[1].temp.max,
              data.daily[2].temp.max,
              data.daily[3].temp.max,
              data.daily[4].temp.max],

        low: [data.daily[0].temp.min,
             data.daily[1].temp.min,
             data.daily[2].temp.min,
             data.daily[3].temp.min,
             data.daily[4].temp.min]
      });
    }
    else{
      alert("Input a city and state separated by a comma");
    }

    setInputText("");
  };

  return (
    <div className="App">
      <header className="App-header">
        <img className="logo" src=""/>
        <form onSubmit={getWeatherData} className="search-bar">
          <img src="https://cdn2.iconfinder.com/data/icons/font-awesome/1792/search-512.png"/>
          <input value={inputText} onChange={inputTextHandler} placeholder="City, State" />
        </form>
      </header>

      <div className="location-ttl"><h3>{form.city}</h3></div>

      <Forecast/>
    </div>
  );
}

export default App;
