import React, {useState} from "react";
import './App.css';

// Import component
import Forecast from "./components/forecast";

const API_key = "fae6fa0ed6e96f99493161cd7089d3a1";

function App() {
  const [inputText, setInputText] = useState("");
  const [form, setState] = useState({
    city: undefined,
    state: undefined
  });

  function inputTextHandler(e){
    var location = e.target.value;

    console.log(location);
    setInputText(location);

    var locArr = location.split(',');
  };

  function updateLocation(){
    setState({
      city: "this City",
      state: "this State"
    });

    console.log(form.city);
    console.log(form.state);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img className="logo" src=""/>
        <form onSubmit={updateLocation} className="search-bar">
          <img src="https://cdn2.iconfinder.com/data/icons/font-awesome/1792/search-512.png"/>
          <input value={inputText} onChange={inputTextHandler} placeholder="City, State" />
        </form>
      </header>

      <div className="location-ttl"><h3>Location</h3></div>

      <Forecast/>
    </div>
  );
}

export default App;
