import React, {useState} from "react";
import './App.css';

// Import component
import Forecast from "./components/forecast";

const API_key = "fae6fa0ed6e96f99493161cd7089d3a1";

function App() {
  const [inputText, setInputText] = useState("");
  const [form, setState] = useState({
    city: undefined,
    state: undefined
  });

  function inputTextHandler(e){

    console.log(e.target.value);
    setInputText(e.target.value);
  };

  async function getWeatherData(e){
    e.preventDefault();
    var location = inputText;

    // make sure user inputs a valid city and state separated by comma
    if(location.includes(',')){
      var locArr = location.split(',');

      var city = locArr[0];
      var state = locArr[1];

      // fetch data from weather api
      const data = await fetch(
        'https://api.openweathermap.org/data/2.5/forecast?q=${city},${state}&appid={API_key}'
      ).then((res) => console.log(res.json()));

      setState({
        city: city,
        state: state
      });
    }
    else{
      alert("Input a city and state separated by a comma");
    }

    setInputText("");
  };



  return (
    <div className="App">
      <header className="App-header">
        <img className="logo" src=""/>
        <form onSubmit={getWeatherData} className="search-bar">
          <img src="https://cdn2.iconfinder.com/data/icons/font-awesome/1792/search-512.png"/>
          <input value={inputText} onChange={inputTextHandler} placeholder="City, State" />
        </form>
      </header>

      <div className="location-ttl"><h3>Location</h3></div>

      <Forecast/>
    </div>
  );
}

export default App;
