import React from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";

// Import component
import Card from "./card";

function Forecast({todayForecast, forecasts, animate}){

    console.log(animate);

    const springProps = useSpring({
        from: {opacity: animate == true ? 0 : null},
        to: {opacity: animate == true ? 1 : null}
    })

    const trailProps = useTrail(6, {
        from: {opacity: animate == true ? 0 : null,
               top: animate == true ? "-25px" : null},
        to: {opacity: 1,
             top: animate == true ? "0px" : null},
        config: {mass: 1,
                 tension: 170,
                 friction: 10}
    })

    animate = false;

    return(
        <div style={{display: todayForecast[0].id == undefined ? "none" : null}} className="container">
            <div className="today-card-container">
                    <animated.div style={{opacity: springProps.opacity}} className="card">
                        <Card
                            key={todayForecast[0].id}
                            day={todayForecast[0].day}
                            timeOfDay={todayForecast[0].timeOfDay}
                            temp={todayForecast[0].temp}
                            cloudType={todayForecast[0].cloudsType}
                            clouds={todayForecast[0].clouds}
                            high={todayForecast[0].high}
                            low={todayForecast[0].low}
                        />
                    </animated.div>
            </div>

            <div className="cards-container">
                {forecasts.map((forecast, index) => (
                     <animated.div style={{top: trailProps[index].top}} className="card">
                        <Card
                            key={forecast.id}
                            day={forecast.day}
                            timeOfDay={forecast.timeOfDay}
                            temp={forecast.temp}
                            cloudType={forecast.cloudsType}
                            clouds={forecast.clouds}
                            high={forecast.high}
                            low={forecast.low}
                        />
                 </animated.div>
                ))
                }
            </div>
        </div>
    );
};

export default Forecast;
