import React from "react";
import { animated, useSpring } from "react-spring";

// Import component
import Card from "./card";

function Forecast({todayForecast, forecasts}){
    const springProps = useSpring({
        from: {opacity: 0},
        to: {opacity: 1}
    });

    return(
        <div className="container" style={{springProps}}>
            <animated.div className="today-card-container">
                {todayForecast.map((today) => (
                    <Card
                        key={today.id}
                        day={today.day}
                        timeOfDay={today.timeOfDay}
                        temp={today.temp}
                        cloudType={today.cloudsType}
                        clouds={today.clouds}
                        high={today.high}
                        low={today.low}
                    />
                ))}
            </animated.div>
            <div className="cards-container">
                {forecasts.map((forecast) => (
                    <Card
                        key={forecasts.id}
                        day={forecast.day}
                        timeOfDay={forecast.timeOfDay}
                        temp={forecast.temp}
                        cloudType={forecast.cloudsType}
                        clouds={forecast.clouds}
                        high={forecast.high}
                        low={forecast.low}
                    />
                ))
                }
            </div>
        </div>
    );
};

export default Forecast;
