import React from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";

// Import component
import Card from "./card";

function Forecast({todayForecast, forecasts}){

    // const trailProps = useTrail({
    //     from: {marginTop: "-75px", opacity: 0},
    //     to: {marginTop: "0px", opactiy: 1}
    // })

    const springProps = useSpring({
        from: {opacity: day=="Today" ? 0 : 1},
        to: {opacity: day=="Today" ? 1 : 1}
    })

    return(
        <div className="container">
            <div className="today-card-container">
                {todayForecast.map((today) => (
                    <animated.div style={{opacity: springProps.opacity, marginTop: springProps.marginTop}} className="card">
                        <Card
                            key={today.id}
                            day={today.day}
                            timeOfDay={today.timeOfDay}
                            temp={today.temp}
                            cloudType={today.cloudsType}
                            clouds={today.clouds}
                            high={today.high}
                            low={today.low}
                        />
                    </animated.div>
                ))}
            </div>

            <div className="cards-container">
                {forecasts.map((forecast) => (
                     <animated.div className="card">
                        <Card
                            key={today.id}
                            day={today.day}
                            timeOfDay={today.timeOfDay}
                            temp={today.temp}
                            cloudType={today.cloudsType}
                            clouds={today.clouds}
                            high={today.high}
                            low={today.low}
                        />
                 </animated.div>
                ))
                }
            </div>
        </div>
    );
};

export default Forecast;
