import React, {useState} from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";
import './App.css';

// Import component
import Forecast from "./components/forecast";
import Suggestions from "./components/suggestions";
import FetchData from "./components/citiesAPI";

const API_key = "fae6fa0ed6e96f99493161cd7089d3a1";

function App() {
  const [inputText, setInputText] = useState("");
  const [cityName, setCityName] = useState("Search for a city...");
  const [todayForecast, setTodayForecast] = useState([0]);
  const [forecasts, setForecasts] = useState([]);
  const [animate, setAnimate] = useState(true);


  <FetchData/>









































  // boolean variable that will tell us if it is day or night
  var isDay;

  function inputTextHandler(e){
    setInputText(e.target.value);

    if(e.target.value == ""){
      if(animate != true){
        setAnimate(true);
      }
    }
    else{
      if(animate != false){
        setAnimate(false);
      }
    }
  };


  async function getWeatherData(e){
    // Clear previous state
    setTodayForecast([0]);
    setForecasts([]);

    e.preventDefault();

    // Animate
    setAnimate(true);

    var location = inputText;

    // make sure user inputs a valid city and state separated by comma
    if(location.includes(',')){
      var locArr = location.split(',');

      var city = locArr[0];
      var state = locArr[1];

      // fetch data from weather api
      const dataToday = await fetch(
        'https://api.openweathermap.org/data/2.5/weather?q='+city+','+state+'&appid='+API_key
      ).then((res) => res.json())
      .then((dataToday) => dataToday);

      setCityName(dataToday.name);

      // Gather coordinates
      var long = dataToday.coord.lon;
      var lat = dataToday.coord.lat;

      const data = await fetch(
        'https://api.openweathermap.org/data/2.5/onecall?lat='+lat+'&lon='+long+'&units=imperial&appid='+API_key
      ).then((res) => res.json())
      .then((data) => data);

      console.log(data);

      // Create array of that will hold converted unix timestamps to weekdays
      var numOfDays = data.daily.length - 1;
      var weekDays = new Array(numOfDays);

      // Loop through array and convert each element from unix to weekday
      for (var i = 0; i < numOfDays; i++){
        var dateObject = new Date(data.daily[i].dt * 1000);
        var day = dateObject.toLocaleString("en-US", {weekday: "long"});

        if(i == 0){
          weekDays[i] = 'Today';
        }
        else{
          weekDays[i] = day;
        }
      }

      if ((Date.now()/1000) > data.daily[0].sunrise && (Date.now()/1000) < data.daily[0].sunset){
        isDay = true;
      }
      else{
        isDay = false;
      }

      // Set current hour temp, clouds, and cloud type
      var currentTemp = data.hourly[0].feels_like;
      var curClouds = data.hourly[0].weather[0].description;
      var curCloudType = data.hourly[0].weather[0].main;


      // Create arrays for temp, clouds, hightemp, lowtemp
      var tempArr = new Array(numOfDays);
      var cloudsTypeArr = new Array(numOfDays);
      var cloudsArr = new Array(numOfDays);
      var highArr = new Array(numOfDays);
      var lowArr = new Array(numOfDays);

      for(var i = 0; i < numOfDays; i++){
        tempArr[i] = data.daily[i].temp.day;
        cloudsTypeArr[i] = data.daily[i].weather[0].main;
        cloudsArr[i] = data.daily[i].weather[0].description;
        highArr[i] = data.daily[i].temp.max;
        lowArr[i] = data.daily[i].temp.min;
      }

      // Set today' forecast
      setTodayForecast([{id: Math.random() * 1000,
        day: weekDays[0],
        timeOfDay: isDay,
        temp: currentTemp,
        cloudsType: curCloudType,
        clouds: curClouds,
        high: highArr[0],
        low: lowArr[0]}])

      // Set week's forecast
      for(var i = 1; i < weekDays.length; i++){
        setForecasts((forecasts) => [...forecasts, {id: Math.random() * 1000,
          day: weekDays[i],
          timeOfDay: true,
          temp: tempArr[i],
          cloudsType: cloudsTypeArr[i],
          clouds: cloudsArr[i],
          high: highArr[i],
          low: lowArr[i]},])
      }
    }
    else{
      alert("Input a city and state separated by a comma");
    }

    setInputText("");
  };

  console.log(cityName);

  return (
    <div className="App">
      <body style={
        isDay == true ? {background: "-webkit-linear-gradient(to right, #91EAE4, #86A8E7, #7F7FD5)",  /* Chrome 10-25, Safari 5.1-6 */
                         background: "linear-gradient(to right, #91EAE4, #86A8E7, #7F7FD5)", /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
                         color: "#000"
        } : {background: "-webkit-linear-gradient(to right, #243B55, #141E30)",  /* Chrome 10-25, Safari 5.1-6 */
             background: "linear-gradient(to right, #243B55, #141E30)", /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
          color: "#fff"
          }
      }>
      <header className="App-header">
        <img className="logo" src=""/>
        <form onSubmit={getWeatherData} className="search-bar"
              style={isDay == true ? {border: "1px #000 solid"} : {border: "1px #fff solid"}}>
          <img src="https://cdn2.iconfinder.com/data/icons/font-awesome/1792/search-512.png"/>
          <input value={inputText} onChange={inputTextHandler} placeholder="City, State"
                  style={isDay == true ? {color: "#000"} : {color: "#fff"}}
                  placeholderTextColor={isDay == true ? "#000" : "#fff"}/>
        </form>
      </header>

      <div className="location-ttl"><h3>{animate == true ? cityName : "Search for a city..."}</h3></div>

      <Suggestions/>

      <Forecast
        todayForecast={todayForecast}
        forecasts={forecasts}
        animate={animate}
        setAnimate={setAnimate}
      />
      </body>
    </div>
  );
}

export default App;
