import React from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";

// Import component
import Card from "./card";
import WeeklyForecast from "./week"

function Forecast({todayForecast, forecasts, springProps}){

    return(
        <div className="container">
            <animated.div className="today-card-container">
                {todayForecast.map((today) => (
                    <Card
                        key={today.id}
                        day={today.day}
                        timeOfDay={today.timeOfDay}
                        temp={today.temp}
                        cloudType={today.cloudsType}
                        clouds={today.clouds}
                        high={today.high}
                        low={today.low}
                    />
                ))}
            </animated.div>

            <WeeklyForecast
                forecasts={forecasts}
            />
        </div>
    );
};

export default Forecast;
