import React from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";

// Import component
import Card from "./card";

function Forecast({todayForecast, forecasts, animate, setAnimate}){

    const [springProps] = useSpring(() => ({
        opacity: 1,
        left: "0px",
        config: {mass: 1,
            tension: 270,
            friction: 25}
    }))

    // set({opacity: animate == true ? 1 : 0,
    //      left: animate == true ? "0px" : "-275px",
    //      from: {opacity: animate == true ? 0 : 1,
    //         left: animate == true ? "-275px" : 0}})

    const [trailProps, set] = useTrail(6, {
        opacity: 1,
        top: "0px",
        config: {mass: 1,
                 tension: 270,
                 friction: 25}
    })

    setTrailProps(6, {opacity: animate == true ? 1 : 0,
                   top: animate == true ? "0px" : "-25px",
                   from: {opacity: animate == true ? 0 : 1,
                          top: animate == true ? "-25px" : "0px"}})

    console.log(trailProps);
    console.log(springProps);

    return(
        <div style={{display: todayForecast[0].id == undefined ? "none" : null}} className="container">
            <div className="today-card-container">
                    <animated.div style={springProps} className="card">
                        <Card
                            key={todayForecast[0].id}
                            day={todayForecast[0].day}
                            timeOfDay={todayForecast[0].timeOfDay}
                            temp={todayForecast[0].temp}
                            cloudType={todayForecast[0].cloudsType}
                            clouds={todayForecast[0].clouds}
                            high={todayForecast[0].high}
                            low={todayForecast[0].low}
                        />
                    </animated.div>
            </div>

            <div className="cards-container">
                {forecasts.map((forecast, index) => (
                     <animated.div style={trailProps[index]} className="card">
                        <Card
                            key={forecast.id}
                            day={forecast.day}
                            timeOfDay={forecast.timeOfDay}
                            temp={forecast.temp}
                            cloudType={forecast.cloudsType}
                            clouds={forecast.clouds}
                            high={forecast.high}
                            low={forecast.low}
                        />
                 </animated.div>
                ))
                }
            </div>
        </div>
    );
};

export default Forecast;
