import React, {useState} from "react";
import './App.css';

// Import component
import Forecast from "./components/forecast";

const API_key = "fae6fa0ed6e96f99493161cd7089d3a1";

function App() {
  const [inputText, setInputText] = useState("");
  const [forecasts, setForecasts] = useState([]);

  var city;

  function inputTextHandler(e){

    console.log(e.target.value);
    setInputText(e.target.value);
  };

  async function getWeatherData(e){
    e.preventDefault();
    var location = inputText;

    // make sure user inputs a valid city and state separated by comma
    if(location.includes(',')){
      var locArr = location.split(',');

      var city = locArr[0];
      var state = locArr[1];

      // fetch data from weather api
      const dataToday = await fetch(
        'https://api.openweathermap.org/data/2.5/weather?q='+city+','+state+'&appid='+API_key
      ).then((res) => res.json())
      .then((dataToday) => dataToday);

      var long = dataToday.coord.lon;
      var lat = dataToday.coord.lat;

      const data = await fetch(
        'https://api.openweathermap.org/data/2.5/onecall?lat='+lat+'&lon='+long+'&units=imperial&appid='+API_key
      ).then((res) => res.json())
      .then((data) => data);

      console.log(data);

      city = dataToday.name;

      // Create array of unix timestamps that will be converted to weekdays
      var unixArr = [data.daily[0].dt, data.daily[1].dt, data.daily[2].dt, data.daily[3].dt, data.daily[4].dt];
      var weekDays = new Array(5);

      // Loop through array and convert each element from unix to weekday
      for (var i = 0; i < unixArr.length; i++){
        var dateObject = new Date(unixArr[i] * 1000);
        var day = dateObject.toLocaleString("en-US", {weekday: "long"});
        weekDays[i] = day;
      }

      var tempArr = [data.daily[0].temp.day, data.daily[1].temp.day, data.daily[2].temp.day, data.daily[3].temp.day, data.daily[4].temp.day];
      var cloudsArr = [data.daily[0].weather[0].description, data.daily[1].weather[0].description, data.daily[2].weather[0].description, data.daily[3].weather[0].description, data.daily[4].weather[0].description];
      var highArr = [data.daily[0].temp.max, data.daily[1].temp.max, data.daily[2].temp.max, data.daily[3].temp.max, data.daily[4].temp.max];
      var lowArr = [data.daily[0].temp.min, data.daily[1].temp.min, data.daily[2].temp.min, data.daily[3].temp.min, data.daily[4].temp.min];

      for(var i = 0; i < weekDays.length; i++){
        // setWeather(weekDays[i], tempArr[i], cloudsArr[i], highArr[i], lowArr[i]);
        setForecasts((forecasts) => [...forecasts, {id: Math.random() * 1000,
          day: weekDays[i],
          temp: tempArr[i],
          clouds: cloudsArr[i],
          high: highArr[i],
          low: lowArr[i]},])
      }
    //   setForecasts([ {id: Math.random() * 1000,
    //                 day: weekDays[0],
    //                 temp: tempArr[0],
    //                 clouds: cloudsArr[0],
    //                 high: highArr[0],
    //                 low: lowArr[0]},

    //                 {id: Math.random() * 1000,
    //                 day: weekDays[1],
    //                 temp: tempArr[1],
    //                 clouds: cloudsArr[1],
    //                 high: highArr[1],
    //                 low: lowArr[1]},

    //                 {id: Math.random() * 1000,
    //                 day: weekDays[2],
    //                 temp: tempArr[2],
    //                 clouds: cloudsArr[2],
    //                 high: highArr[2],
    //                 low: lowArr[2]},

    //                 {id: Math.random() * 1000,
    //                 day: weekDays[3],
    //                 temp: tempArr[3],
    //                 clouds: cloudsArr[3],
    //                 high: highArr[3],
    //                 low: lowArr[3]},

    //                 {id: Math.random() * 1000,
    //                 day: weekDays[4],
    //                 temp: tempArr[4],
    //                 clouds: cloudsArr[4],
    //                 high: highArr[4],
    //                 low: lowArr[4]},
    //                 ]);
    }
    else{
      alert("Input a city and state separated by a comma");
    }

    setInputText("");
  };

  // Add all 5 day forecasts to state
  function setWeather(weekDays, tempArr, cloudsArr, highArr, lowArr){
      setForecasts([...forecasts, {id: Math.random() * 1000,
        day: weekDays,
        temp: tempArr,
        clouds: cloudsArr,
        high: highArr,
        low: lowArr},]);
  }

  console.log(forecasts);

  return (
    <div className="App">
      <header className="App-header">
        <img className="logo" src=""/>
        <form onSubmit={getWeatherData} className="search-bar">
          <img src="https://cdn2.iconfinder.com/data/icons/font-awesome/1792/search-512.png"/>
          <input value={inputText} onChange={inputTextHandler} placeholder="City, State" />
        </form>
      </header>

      <div className="location-ttl"><h3>{city}</h3></div>

      <Forecast
        forecasts={forecasts}
      />
    </div>
  );
}

export default App;
