import React from "react";
import { useSpring, useTrail, animated, interpolate } from "react-spring";

// Import component
import Card from "./card";

function Forecast({todayForecast, forecasts}){

    // const trailProps = useTrail({
    //     from: {marginTop: "-75px", opacity: 0},
    //     to: {marginTop: "0px", opactiy: 1}
    // })

    const springProps = useSpring({
        from: {opacity: 0},
        to: {opacity: 1}
    })

    return(
        <div className="container">
            <div className="today-card-container">
                    <animated.div style={{opacity: springProps.opacity, marginTop: springProps.marginTop}} className="card">
                        <Card
                            key={todayForecast.id}
                            day={todayForecast.day}
                            timeOfDay={todayForecast.timeOfDay}
                            temp={todayForecast.temp}
                            cloudType={todayForecast.cloudsType}
                            clouds={todayForecast.clouds}
                            high={todayForecast.high}
                            low={todayForecast.low}
                        />
                    </animated.div>
            </div>

            <div className="cards-container">
                {forecasts.map((forecast) => (
                     <animated.div className="card">
                        <Card
                            key={forecast.id}
                            day={forecast.day}
                            timeOfDay={forecast.timeOfDay}
                            temp={forecast.temp}
                            cloudType={forecast.cloudsType}
                            clouds={forecast.clouds}
                            high={forecast.high}
                            low={forecast.low}
                        />
                 </animated.div>
                ))
                }
            </div>
        </div>
    );
};

export default Forecast;
